mapper.select:
  select count(1) from fukuang where dsz=2 and lszt=2

    and room = #{cxname}


    and sfren = #{cxname}

select szId,room, sfren,
zdMoney, zdType,
sfDate, zfFangshi,
dsz, lszt, beizhu,
date_format(zfdate, '%Y-%m-%d') as zfdate, startdate,
entdate, sfmoney from fukuang where dsz=2 and lszt=2

    and room = #{cxname}


    and sfren = #{cxname}

  limit #{start},#{pageSize}
